import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { useNavigate } from "react-router-dom";

function Admin(){
    const [artistName, setArtistName] = useState('');
    const [artistImage, setArtistImage] = useState(''); 
    const [passwordType, setPasswordType] = useState('password')
    const [toggle, setToggle] = useState('SHOW')
    const [loginStatus, setLoginStatus] = useState('');

    Axios.defaults.withCredentials = true
    const addArtist = () => {
        Axios.post('http://localhost:7000/admin', {
        artistName: artistName, 
        artistImage: artistImage
        }).then((response) => {
            console.log(response.data)
            if(response.data.message){
                setLoginStatus(response.data.message);
            }else{
                setLoginStatus("Welcome "+response.data[0].u_name);
            }
        })
    }

    const togglePasswordType = () => {
        if(passwordType === 'password'){
            setPasswordType('text')
            setToggle('HIDE')
            return;
        }
        setPasswordType('password')
        setToggle('SHOW')
    }

    useEffect(() => {
        Axios.get('http://localhost:7000/admin').then((response) => {
            if(response.data.admin === true){
                console.log("Admin")
            }else{
                navigate("/")
            }
        })
    }, [])

    return (
        <div >
          <div>
            <h1>Artist</h1>
            <h2>Add:</h2>
                <input type='text' placeholder='name' onChange={(e)=>{
                setArtistName(e.target.value);
                }}/><br/>
                <input type='text' placeholder='image-url' onChange={(e)=>{
                setArtistImage(e.target.value);
                }}/><br/>
                <button onClick={addArtist}>Add Artist</button>
          </div>
          <div>
            <h1>Genre</h1>
            <h2>Add:</h2>
                <input type='text' placeholder='name'></input><br/>
                <input type='text' placeholder='image-url'></input><br/>
          </div>
          <div>
            <h1>Song</h1>
          </div>
        </div>
    )
}

export default Admin;
