import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { useNavigate } from "react-router-dom";

function Admin(){
    const [artistName, setArtistName] = useState('');
    const [artistImage, setArtistImage] = useState(''); 
    const [artistOperation, setArtistOperation] = useState('')
    const [loginStatus, setLoginStatus] = useState('');

    const navigate = useNavigate();
    Axios.defaults.withCredentials = true
    const addArtist = () => {
        setArtistOperation("add")
        Axios.post('http://localhost:7000/admin', {
        operation: artistOperation,
        artistName: artistName, 
        artistImage: artistImage
        }).then((response) => {
            console.log(response.data)
            if(response.data.message){
                setLoginStatus(response.data.message);
            }else{
                setLoginStatus("Welcome "+response.data[0].u_name);
            }
        })
    }

    const logout = () => {
        setLoginStatus(false)
        Axios.post('http://localhost:7000/admin', {
        admin: loginStatus
        }).then((response) => {
            console.log(response.data)
            if(response.data.message){
                setLoginStatus(response.data.message);
            }else{
                setLoginStatus("Welcome "+response.data[0].u_name);
            }
        })
    }

    useEffect(() => {
        Axios.get('http://localhost:7000/admin').then((response) => {
            if(response.data.admin === true){
                console.log("Admin")
            }else{
                navigate("/")
            }
        })
    }, [])

    return (
        <div >
         <div><button onClick={logout}>Logout</button></div>
          <div>
            <h1>Artist</h1>
            <h2>Add:</h2>
                <input type='text' placeholder='name' onChange={(e)=>{
                setArtistName(e.target.value);
                }}/><br/>
                <input type='text' placeholder='image-url' onChange={(e)=>{
                setArtistImage(e.target.value);
                }}/><br/>
                <button onClick={addArtist}>Add Artist</button>
          </div>
          <div>
            <h1>Genre</h1>
            <h2>Add:</h2>
                <input type='text' placeholder='name'></input><br/>
                <input type='text' placeholder='image-url'></input><br/>
          </div>
          <div>
            <h1>Song</h1>
          </div>
        </div>
    )
}

export default Admin;
