import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function Login(){
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState(''); 
    const [passwordType, setPasswordType] = useState('password')

    const [loginStatus, setLoginStatus] = useState('');

    Axios.defaults.withCredentials = true
    const login = () => {
        Axios.post('http://localhost:7000/login', {
        username: username, 
        password: password
        }).then((response) => {
            console.log(response.data)
            if(response.data.message){
                setLoginStatus(response.data.message);
            }else{
                setLoginStatus("Welcome "+response.data[0].u_name);
            }
        })
    }

    const togglePasswordType = () => {
        if(passwordType === 'password'){
            setPasswordType('text')
            return;
        }
        setPasswordType('password')
    }

    useEffect(() => {
        Axios.get('http://localhost:7000/login').then((response) => {
            if(response.data.loggedIn == true){
                setLoginStatus(response.data.user[0].u_name)
            }
        })
    }, [])

    return (
        <div>
           <h1>Login</h1>
           <label>Username:</label>
            <input type="text" name="username" onChange={(e)=>{
                setUsername(e.target.value);
            }}/>
            <br />
            <label>Password:</label>
            <input type={passwordType} name="password" onChange={(e)=>{
                setPassword(e.target.value);
            }}/>
            <button onClick={togglePasswordType}>Toggle</button>
            <br />
            <button onClick={login}>Login</button>
           <p>Don't have an account? <a href='/register'>Register</a></p>

            <h1>{loginStatus}</h1>

        </div>
    )
}

export default Login;
